def create(n):
    Board = [[0 for i in range(n)] for j in range(n)]
    if arg.board[:6] == 'random':
            Board = randomBoard(Board,int(arg.board[9:]))
    elif seeds[arg.board]:
        Board = seededBoard(Board,arg.board)
    else:
        print('Incorrect Seed')
    return Board

def randomBoard(Board,chance):
    for i in range(len(Board[0])):
        for j in range(len(Board)):
            k = random.randint(1,chance)
            if k == 1:
                Board[i][j] = 1
    return Board

def seededBoard(Board,seed):
    try:
        ob = seeds[seed]
        a = int((len(Board[0])-len(ob[0]))/2)
        b = int((len(Board)-len(ob))/2)
        for j in range(len(ob[0])):
            for i in range(len(ob)):
                Board[a+i][b+j] = ob[i][j]
        return Board              
    except:
        print('Board size is to small for this seed!')
        exit()


def generation(Board):
    for i in range(len(Board[0])):
        for j in range(len(Board)):
            if Board[i][j]%10 == 1:
                for a in range(-1,2):
                    for b in range(-1,2):
                        if a == 0 and b == 0:
                            continue
                        elif i+a < 0 or j+b < 0:
                                continue
                        try:
                            Board[i+a][j+b] += 10
                        except:
                            continue
                                         
    for i in range(len(Board[0])):
        for j in range(len(Board)):
            if Board[i][j] == 30 or Board[i][j] == 31 or Board[i][j] == 21:
                Board[i][j] = 1
            else:
                Board[i][j] = 0
    return Board



def animateBoard(Board,save,speed,gen):
    N = len(Board) #Size of Board
    fig, ax = plt.subplots(tight_layout=True)

    # make color map
    my_cmap = matplotlib.colors.ListedColormap(['w', 'g'])
    ax.axis('off')

    ims = []
    for i in range(gen):
        data = ax.imshow(Board, cmap=my_cmap, extent=[0, N, 0, N])
        ims.append((data,))
        Board = generation(Board)
    im_ani = animation.ArtistAnimation(
        fig, ims, interval=speed, repeat_delay=300, blit=True
    )
    plt.show()
    if save:
        name = input("Give name for Gif:")
        im_ani.save((name + ".gif"))




if __name__ == "__main__":
    
    parser = argparse.ArgumentParser(
        description="Game of Life. By default produces 50 Generations of a randomly generated Board"
    )
    
    parser.add_argument(
        "-size",
        type=int,
        default=100,
        help="Integer n that gives size of the universe. Always n*n"
    )

    parser.add_argument(
        "-iter",
        type=int,
        default = 100,
        help="Integer. Number of iterations."
    )
    
    parser.add_argument(
        "-save",
        type=bool,
        default = False,
        help="True if you want to save a Gif of the game."
    )
    
    parser.add_argument(
        "-speed",
        type=int,
        default=100,
        help="Time for each generation in milliseconds"
    )
    
    parser.add_argument(
        "-board",
        type = str,
        default = "random,1/10",
        help="Choose which starting Position. Check the documentation for a specific seed otherwise type 'random,1/X' where 1/X defines the likelyhood of a square being alive"
    )
    
    arg = parser.parse_args()
    Board = create(arg.size)
    animateBoard(
        Board,
        arg.save,
        arg.speed,
        arg.iter
        )

